INSERT INTO `quality_assurance` VALUES (1,'<p>About CPI</p>\r\n','<p>Quality Assurance</p>\r\n','<p>Maintaining effective Quality Management System through improvement &amp; achieving regulatory compliance by meeting customer requirement &amp; provide quality products for total customer satisfaction.</p>\r\n\r\n<p>Our quality philosophy is supported by our core values:</p>\r\n\r\n<div class=\"list list-style__star\">\r\n<ul>\r\n	<li>Commit to Fulfill Total Customer Satisfaction.</li>\r\n	<li>Value Our People as the Most Important Asset of the Company.</li>\r\n	<li>Believe in Personal Commitment to Professionalism and Excellence.</li>\r\n	<li>Promote Teamwork, Personal Leadership and a Passion for Quality.</li>\r\n	<li>Encourage Innovation and Continual Improvement in everything we do.</li>\r\n</ul>\r\n</div>\r\n\r\n<p>CPI is registered with Underwriter Laboratory UL. All manufacturing materials are RoHS &amp; REACH compliance.</p>\r\n<!-- end clearfix --><!-- end grid 12 --><!-- Horizontal Rule (double) --><!-- Horizontal Rule (double) / End -->\r\n\r\n<h3>Process Validation Capability</h3>\r\n\r\n<div class=\"list list-style__star\">\r\n<ul>\r\n	<li>Protocol development and Validation Master Plan.</li>\r\n	<li>Resources (Machine, Man) are allocated to perform DOE &amp; GRR for worst case study and optimization during OQ stage.</li>\r\n	<li>Achieve Statistical Process Capability of &gt;1.67 for critical parameters at PQ before mass production.</li>\r\n	<li>Use Statistical tools (Minitab Ver.16.2) for data analysis.</li>\r\n	<li>Process Equipment Validation during IQ and OQ are performed to ensure equipment is operated as desired.</li>\r\n	<li>Use advance test equipment for Validation.</li>\r\n</ul>\r\n</div>\r\n<!-- end grid 12 -->\r\n\r\n<h3>International Certification</h3>\r\n<!-- Horizontal Rule (double) --><!-- Horizontal Rule (double) / End -->\r\n\r\n<div class=\"list list-style__star\">\r\n<ul>\r\n	<li>\r\n	<p>AS 9100: Aerospace Quality System (Plan in 2020)</p>\r\n	</li>\r\n	<li>\r\n	<p><a href=\"/assets/admin/vendors/upload/files/ISO_13485_2016.pdf\" target=\"_blank\">ISO 13485:2016</a> (Certified in Jun 2012)</p>\r\n	</li>\r\n	<li>\r\n	<p><a href=\"/assets/admin/vendors/upload/files/ISO_14001_2015.pdf\" target=\"_blank\">ISO 14001:2015</a> (Certified in Jan 2010)</p>\r\n	</li>\r\n	<li>\r\n	<p><a href=\"/assets/admin/vendors/upload/files/IATF_16949_2016.pdf\" target=\"_blank\">IATF 16949:2016</a> (Certified in Oct 2005)</p>\r\n	</li>\r\n	<li>\r\n	<p><a href=\"/assets/admin/vendors/upload/files/ISO_9001_2015.pdf\" target=\"_blank\">ISO 9001:2015</a> (Certified in 1995 Upgraded in 2000)</p>\r\n	</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3><strong>Quality Assuarance Policy</strong></h3>\r\n\r\n<ul>\r\n	<li><a href=\"/assets/admin/vendors/upload/files/Supplier%20Quality%20Agreement%20Rev%205.pdf\">Supplier Quality Agreement</a></li>\r\n	<li><a href=\"/assets/admin/vendors/upload/files/GPR%20-%20FRM-MAT-030%20REV%2015.pdf\">General Purchase Requirements</a></li>\r\n</ul>\r\n</div>\r\n<!-- end clearfix -->','74461e64f6d6ea01d7153fd612bfc385.pdf','712f626b9220aab033351357e374c945.png','2019-12-04 07:57:21');
